CREATE OR REPLACE PROCEDURE lab_informa_motivo_desaprov_js ( nr_seq_motivo_desaprov_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nm_usuario_p text, ie_lib_p text, ie_Informa_obs_p text) AS $body$
BEGIN

if (coalesce(ie_lib_p,'N') = 'N') then
	update	prescr_procedimento
	set		nr_seq_motivo_desaprov = nr_seq_motivo_desaprov_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia = nr_seq_prescr_p;

    if (ie_Informa_obs_p = 'S')then
		INSERT INTO Prescr_proc_desaprov(nr_sequencia,
					nr_seq_Motivo_desaprov,
					nr_prescricao,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_prescr)
			VALUES (nextval('prescr_proc_desaprov_seq'),
					nr_seq_motivo_desaprov_p,
					nr_prescricao_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_prescr_p);
	end if;
else
	update	prescr_procedimento
	set		nr_seq_motivo_deslib_ex = nr_seq_motivo_desaprov_p,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia = nr_seq_prescr_p;
end if;

commit;

end;
$body$
