CREATE OR REPLACE PROCEDURE lab_informa_motivo_desaprov ( nr_seq_motivo_desaprov_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nm_usuario_p text, ie_lib_p text, ds_observ_desaprov_p text) AS $body$
