SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_insere_result_lab_pdf ( nr_prescricao_p text, nr_seq_prescr_p text, ds_pdf_serial_p text, nm_usuario_p text, ds_erro_p INOUT text, nr_seq_result_pdf_p INOUT bigint) AS $body$
DECLARE


nr_seq_result_lab_w	bigint;
nr_seq_exame_w		bigint;
ie_formato_texto_w	smallint;
nr_seq_result_pdf_w	result_laboratorio_pdf.nr_sequencia%type;


BEGIN

select 	MAX(nr_sequencia),
		MAX(coalesce(ie_formato_texto,1))
into STRICT 	nr_seq_result_lab_w,
		ie_formato_texto_w
from 	result_laboratorio
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescr_p;

select 	MAX(nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia = nr_seq_prescr_p;

if (coalesce(nr_seq_result_lab_w::text, '') = '') then

	insert into result_laboratorio(
		nr_sequencia,
		ds_resultado,
		nr_prescricao,
		nr_seq_prescricao,
		nm_usuario,
		dt_atualizacao,
		ie_formato_texto,
		ie_cobranca)
	values (nextval('result_laboratorio_seq'),
		obter_desc_expressao(621294),
		nr_prescricao_p,
		nr_seq_prescr_p,
		nm_usuario_p,
		clock_timestamp(),
		3,
		'N');
else
	update 	result_laboratorio
	set 	ds_resultado = obter_desc_expressao(621294),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where 	nr_prescricao = nr_prescricao_p
	and		nr_seq_prescricao = nr_seq_prescr_p
	and		coalesce(ds_resultado::text, '') = '';
end if;

select 	MAX(nr_sequencia),
		MAX(coalesce(ie_formato_texto,1))
into STRICT 	nr_seq_result_lab_w,
		ie_formato_texto_w
from 	result_laboratorio
where	nr_prescricao	= nr_prescricao_p
and		nr_seq_prescricao = nr_seq_prescr_p;


if (nr_seq_result_lab_w IS NOT NULL AND nr_seq_result_lab_w::text <> '') then
	begin

	if (ie_formato_texto_w <> 3) then
		update 	result_laboratorio
		set 	ie_formato_texto = 3,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
		where 	nr_prescricao = nr_prescricao_p
		and		nr_seq_prescricao = nr_seq_prescr_p;

		commit;

	end if;


	select max(nr_sequencia)
	into STRICT nr_seq_result_pdf_w
	from result_laboratorio_pdf
	where nr_seq_resultado = nr_seq_result_lab_w
	 and  nr_seq_prescricao = nr_seq_prescr_p;

	if (nr_seq_result_pdf_w IS NOT NULL AND nr_seq_result_pdf_w::text <> '') then
		update result_laboratorio_pdf
		   set dt_atualizacao = clock_timestamp(),
			   nm_usuario = nm_usuario_p,
			   ds_pdf_serial = ds_pdf_serial_p
   where nr_seq_resultado = nr_seq_result_lab_w
     and nr_seq_prescricao = nr_seq_prescr_p;
	else
		select 	nextval('result_laboratorio_pdf_seq')
		into STRICT	nr_seq_result_pdf_w
		;

		insert into result_laboratorio_pdf(	nr_sequencia,
						nr_prescricao,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_prescricao,
						nr_seq_resultado,
						nr_seq_exame,
						ds_pdf_serial
						)
		values (nr_seq_result_pdf_w	,
						nr_prescricao_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_prescr_p,
						nr_seq_result_lab_w,
						nr_seq_exame_w,
						ds_pdf_serial_p
						);
	end if;


	nr_seq_result_pdf_p := nr_seq_result_pdf_w;

	exception
		when others then
			ds_erro_p := SQLERRM(SQLSTATE);
		end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lab_insere_result_lab_pdf ( nr_prescricao_p text, nr_seq_prescr_p text, ds_pdf_serial_p text, nm_usuario_p text, ds_erro_p INOUT text, nr_seq_result_pdf_p INOUT bigint) FROM PUBLIC;

