SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_inserir_cod_equip (cd_equipamento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/*
CI - Codigo de Integração
CE - Codigo do Exame
*/
cd_exame_integracao_w	varchar(20);
cd_exame_w		varchar(20);
nr_seq_exame_w		bigint;

C01 CURSOR FOR
	SELECT 	a.cd_exame_integracao,
		a.nr_seq_exame
	from 	exame_laboratorio a
	where	(a.cd_exame_integracao IS NOT NULL AND a.cd_exame_integracao::text <> '')
	and	not exists (SELECT 1
		  	  from  lab_exame_equip b
			  where b.cd_exame_equip = a.cd_exame_integracao
			  and	b.nr_seq_exame	 = a.nr_seq_exame
			  and	b.cd_equipamento = cd_equipamento_p);

C02 CURSOR FOR
	SELECT 	a.nr_seq_exame,
		a.cd_exame
	from 	exame_laboratorio a
	where	coalesce(a.cd_exame_integracao::text, '') = ''
	and	(a.cd_exame IS NOT NULL AND a.cd_exame::text <> '')
	and	not exists (SELECT 1
		  	  from  lab_exame_equip b
			  where b.cd_exame_equip = a.cd_exame
			  and	b.nr_seq_exame	 = a.nr_seq_exame
			  and	b.cd_equipamento = cd_equipamento_p);


BEGIN
if (ie_opcao_p = 'CI') then
	OPEN C01;
	LOOP
	FETCH C01 	into 	cd_exame_integracao_w,
				nr_seq_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	insert into	lab_exame_equip(NR_SEQUENCIA,
					NR_SEQ_EXAME,
					CD_EQUIPAMENTO,
					CD_EXAME_EQUIP,
					IE_PADRAO,
					DT_ATUALIZACAO,
					NM_USUARIO,
					DT_ATUALIZACAO_NREC,
					NM_USUARIO_NREC)
			values (nextval('lab_exame_equip_seq'),
					nr_seq_exame_w,
					cd_equipamento_p,
				        cd_exame_integracao_w,
					'S',
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p);
	END LOOP;
	CLOSE C01;
else
	OPEN C02;
	LOOP
	FETCH C02 	into 	nr_seq_exame_w,
				cd_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	insert into	lab_exame_equip(NR_SEQUENCIA,
					NR_SEQ_EXAME,
					CD_EQUIPAMENTO,
					CD_EXAME_EQUIP,
					IE_PADRAO,
					DT_ATUALIZACAO,
					NM_USUARIO,
					DT_ATUALIZACAO_NREC,
					NM_USUARIO_NREC)
			values (nextval('lab_exame_equip_seq'),
					nr_seq_exame_w,
					cd_equipamento_p,
				        cd_exame_w,
					'S',
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p);
	END LOOP;
	CLOSE C02;

end if;

commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
