CREATE OR REPLACE PROCEDURE lab_inserir_exame_lote ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_lote_ext_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_lote_ext_w
from	lab_lote_exame_item a
where	a.nr_prescricao = nr_prescricao_p
and	a.nr_seq_prescr = nr_seq_prescr_p;

if (ie_lote_ext_w = 'S') then

	DELETE FROM lab_lote_exame_item a
	WHERE	a.nr_prescricao = nr_prescricao_p
	and		a.nr_seq_prescr = nr_seq_prescr_p;

end if;

insert into lab_lote_exame_item(
		NR_SEQ_LOTE,
		NR_PRESCRICAO,
		NR_SEQ_PRESCR,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC)
	values (	nr_seq_lote_p,
		nr_prescricao_p,
		nr_seq_prescr_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p);

commit;
end;
$body$
