CREATE OR REPLACE PROCEDURE lab_ins_lote_ent_result_ord_hb ( cd_barras_p text, nr_corrida_p bigint, cd_exame_p text, nm_usuario_p text, cd_estabelecimento_p text) AS $body$
DECLARE



ds_texto_padrao_w		varchar(255);
ie_formato_resultado_w	varchar(3);
ds_resultado_w			varchar(4000);
ds_regra_w				varchar(255);
nr_prescricao_w			bigint;



BEGIN
if (nr_corrida_p IS NOT NULL AND nr_corrida_p::text <> '') then

	select 	max(ds_texto_padrao),
			max(ie_formato_resultado),
			max(ds_regra)
	into STRICT	ds_texto_padrao_w,
			ie_formato_resultado_w,
			ds_regra_w
	from	exame_laboratorio
	where	coalesce(Obter_Equipamento_Exame(nr_seq_exame,null,'PERKINS'), coalesce(cd_exame_integracao,cd_exame)) = cd_exame_p;

	if (substr(ie_formato_resultado_w,1,1) = 'V') then
		ds_resultado_w := 0;
	elsif (substr(ie_formato_resultado_w,1,1) = 'D') then
		ds_resultado_w := '';
	else
		ds_resultado_w := 0;
	end if;

	if (ds_texto_padrao_w <> '') or (ds_texto_padrao_w <> '0') then
		ds_resultado_w := coalesce(lab_obter_valor_padrao(ds_regra_w),ds_texto_padrao_w);
	end if;

	insert into lote_ent_result_ord_hb(nr_sequencia,
									cd_estabelecimento,
									dt_atualizacao,
									nm_usuario,
									dt_atualizacao_nrec,
									nm_usuario_nrec,
									nr_corrida,
									cd_exame,
									cd_barras,
									ds_resultado)
								values (nextval('lote_ent_result_ord_hb_seq'),
									cd_estabelecimento_p,
									clock_timestamp(),
									nm_usuario_p,
									clock_timestamp(),
									nm_usuario_p,
									nr_corrida_p,
									cd_exame_p,
									cd_barras_p,
									ds_resultado_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
