CREATE OR REPLACE PROCEDURE lab_liberar_exame_amostra ( nr_prescricao_p bigint, nr_seq_resultado_p bigint, nr_seq_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prescr_w		bigint;

c01 CURSOR FOR
	SELECT  a.nr_sequencia
	from	prescr_procedimento a,
		exame_laboratorio b,
		exame_lab_resultado c,
		exame_lab_result_item d
	where	b.nr_seq_exame = a.nr_seq_exame
	and	c.nr_prescricao = a.nr_prescricao
	and	c.nr_seq_resultado = d.nr_seq_resultado
	and 	d.nr_seq_prescr = a.nr_sequencia
	and	a.ie_status_atend = 35
	and	a.nr_prescricao = nr_prescricao_p
	and 	d.nr_seq_material = nr_seq_material_p;

BEGIN

open c01;
loop
fetch c01 into
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update 	exame_lab_result_item
	set 	dt_liberacao = clock_timestamp(),
		nm_usuario_liberacao = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where 	nr_seq_resultado = nr_seq_resultado_p
	and 	nr_seq_prescr = nr_seq_prescr_w
	and 	(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');

	update 	prescr_procedimento
	set 	ie_status_atend = 40
	where 	nr_prescricao = nr_prescricao_p
	and 	nr_sequencia = nr_seq_prescr_w
	and 	ie_status_atend = 35;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
