CREATE OR REPLACE PROCEDURE lab_marcar_exames_lido ( nr_prescricao_p text, nr_seq_prescricao_p text) AS $body$
DECLARE


nr_seq_result_w		bigint;
nr_seq_result_acesso_w	bigint;

