	SELECT	nr_sequencia
	from	result_laboratorio
	where	nr_prescricao = nr_prescricao_p
	and (nr_seq_prescricao_p = '0')
	and		Obter_Status_Exame(nr_prescricao, nr_seq_prescricao) >=35
	order by nr_sequencia;


BEGIN

	open C01;
