	fetch C01 into
		nr_seq_result_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin


		select 	max(nr_sequencia)+1
		into STRICT 	nr_seq_result_acesso_w
		from	result_laboratorio_acesso;

		insert into result_laboratorio_acesso(	nr_sequencia,
												nr_seq_result_lab,
												ie_status,
												dt_atualizacao,
												nm_usuario)
											values (nr_seq_result_acesso_w,
													nr_seq_result_w,
													'2',
													clock_timestamp(),
													'LabWeb');
		end;
	end loop;
	close C01;

commit;

end;
