SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_pda_imprime_etiqueta ( nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_impressora_p bigint, cd_pessoa_fisica_p text, ds_erro_p INOUT text) AS $body$
DECLARE



ds_endereco_rede_w     	varchar(255);
ds_endereco_ip_w	varchar(255);
nr_controle_w		bigint;


BEGIN

select 	max(ds_endereco_rede),
	max(ds_endereco_ip)
into STRICT	ds_endereco_rede_w,
	ds_endereco_ip_w
from	impressora
where	nr_sequencia = nr_seq_impressora_p;


if (coalesce(ds_endereco_rede_w::text, '') = '' or coalesce(ds_endereco_ip_w::text, '') = '') then
	ds_erro_p := wheb_mensagem_pck.get_texto(279101);
else
	select 	CASE WHEN coalesce(ie_agrupa_ficha_fleury,'N')='N' THEN  a.nr_controle  ELSE b.nr_controle_ext END
	into STRICT 	nr_controle_w
	from 	prescr_medica a,
		prescr_procedimento b,
		lab_parametro c
	where 	a.nr_prescricao = b.nr_prescricao
	and	a.cd_estabelecimento = c.cd_estabelecimento
	and	b.nr_prescricao = nr_prescricao_p
	and 	b.nr_sequencia = nr_seq_prescr_p;

	insert into pda_impressao_etiqueta(
		nr_sequencia,
		nr_atendimento,
		nr_prescricao,
		nr_seq_prescr,
		nr_seq_impressora,
		nr_controle,
		cd_pessoa_fisica,
		dt_leitura,
		dt_inclusao)
	values (nextval('pda_impressao_etiqueta_seq'),
		nr_atendimento_p,
		nr_prescricao_p,
		nr_seq_prescr_p,
		nr_seq_impressora_p,
		nr_controle_w,
		cd_pessoa_fisica_p,
		clock_timestamp(),
		clock_timestamp());

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
