CREATE OR REPLACE PROCEDURE lab_registrar_ocorrencia_ficha ( nr_seq_lote_sec_p bigint, nr_seq_motivo_inadeq_p bigint, nr_seq_tipo_ocorr_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_prescr_w	bigint;


BEGIN

if (nr_seq_lote_sec_p	> 0) then
	update	lote_ent_sec_ficha
	set	nr_seq_mot_inadeq	= nr_seq_motivo_inadeq_p,
		ie_status_ficha		= 'P',
		ds_motivo_inadeq	= ds_motivo_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_lote_sec_p;
end if;

if (coalesce(nr_seq_tipo_ocorr_p,0) > 0) then
	insert into lote_ent_ocorrencia(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_situacao,
		nr_seq_tipo_ocorrencia,
		nr_seq_mot_inadeq)
	values (nextval('lote_ent_ocorrencia_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		'A',
		nr_seq_tipo_ocorr_p,
		nr_seq_motivo_inadeq_p);
end if;

commit;

end;
$body$
