CREATE OR REPLACE PROCEDURE lab_registra_dt_integracao ( nr_prescricao_p text, ds_amostra_p text, ie_status_pos_envio_p text, nm_usuario_p text) AS $body$
DECLARE


ie_padrao_amostra_w		varchar(5);
nr_seq_prescricao_w		varchar(255);
nr_seq_prescricao_concat_w	varchar(255);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	trim(both Lab_obter_amostra_prescr_int(ie_padrao_amostra_w,nr_prescricao,nr_seq_exame,nr_sequencia)) = trim(both ds_amostra_p)
	order by 1;


BEGIN

select 	MAX(ie_padrao_amostra)
into STRICT	ie_padrao_amostra_w
from	lab_parametro a,
	prescr_medica b
where 	a.cd_estabelecimento = b.cd_estabelecimento
and	b.nr_prescricao = nr_prescricao_p;

open C01;
loop
fetch C01 into
	nr_seq_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
--		log  tasy(89,'111111nr_prescricao_p'||nr_prescricao_p||'nr_seq_prescricao_concat_w'||nr_seq_prescricao_concat_w,'teste');
--		commit;
		nr_seq_prescricao_concat_w := nr_seq_prescricao_concat_w||nr_seq_prescricao_w||',';
	end;
end loop;
close C01;

select substr(nr_seq_prescricao_concat_w,1,length(nr_seq_prescricao_concat_w)-1)
into STRICT	nr_seq_prescricao_concat_w
;

--log  tasy(89,'nr_prescricao_p'||nr_prescricao_p||'nr_seq_prescricao_concat_w'||nr_seq_prescricao_concat_w,'teste');
--commit;
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_concat_w IS NOT NULL AND nr_seq_prescricao_concat_w::text <> '')then

	update 	prescr_procedimento
	set  	dt_integracao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp() ,
		ie_status_atend = coalesce(ie_status_pos_envio_p,ie_status_atend)
	where 	nr_prescricao = nr_prescricao_p
	and	obter_se_contido(nr_sequencia,nr_seq_prescricao_concat_w) = 'S';

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
