CREATE OR REPLACE PROCEDURE lab_reg_entrega_exame_status ( nr_seq_prescr_p text, nr_prescricao_p bigint, cd_pessoa_fisica_p text, nm_pessoa_entrega_p text, ds_observacao_p text, dt_entrega_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


ie_status_atend_w		smallint;
ie_status_permitido_w 	smallint;


BEGIN

ie_status_permitido_w := obter_valor_param_usuario(723, 69, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);

if (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select  ie_status_atend
	into STRICT	ie_status_atend_w
	from	prescr_procedimento
	where	nr_sequencia	= nr_seq_prescr_p
	and	nr_prescricao	= nr_prescricao_p;

	if (ie_status_atend_w in (35,40)) then

		update	prescr_procedimento
		set		ie_status_atend	= 45,
				dt_atualizacao  = clock_timestamp(),
				nm_usuario      = nm_usuario_p
		where	ie_status_atend >= ie_status_permitido_w
		and	nr_prescricao = nr_prescricao_p;

	end if;

	insert	into lab_entrega_exame(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_prescricao,
		nr_seq_prescr,
		cd_pessoa_fisica,
		nm_pessoa_entrega,
		ds_observacao,
		dt_entrega,
		ie_situacao)
	values (
		nextval('lab_entrega_exame_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_prescricao_p,
		nr_seq_prescr_p,
		cd_pessoa_fisica_p,
		nm_pessoa_entrega_p,
		ds_observacao_p,
		dt_entrega_p,
		'A');
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
