CREATE OR REPLACE PROCEDURE lab_resetar_senha_web ( nr_atendimento_p lab_login_web.ds_usuario_web%TYPE, cd_pessoa_fisica_p lab_login_web.ds_usuario_web%TYPE, nr_prescricao_p prescr_medica.nr_prescricao%TYPE default null, nr_crm_p medico.nr_crm%TYPE default null, uf_crm_p medico.uf_crm%TYPE default null) AS $body$
DECLARE


nr_atendimento_w     lab_login_web.ds_usuario_web%TYPE := nr_atendimento_p;
cd_pessoa_fisica_w   lab_login_web.ds_usuario_web%TYPE := cd_pessoa_fisica_p;
nr_crm_uf_w          lab_login_web.ds_usuario_web%TYPE := nr_crm_p || '-' || uf_crm_p;


BEGIN
    if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '')
    and (coalesce(cd_pessoa_fisica_w::text, '') = '')
    and (coalesce(nr_atendimento_w::text, '') = '') then
       select nr_atendimento,
              cd_pessoa_fisica
         into STRICT nr_atendimento_w,
              cd_pessoa_fisica_w
         from prescr_medica
        where nr_prescricao = nr_prescricao_p;
    end if;

    IF (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') THEN
        DELETE FROM lab_login_web
        WHERE  ds_usuario_web = nr_atendimento_w
          AND  ie_tipo_login = 'A';

    END IF;

    IF (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') THEN
        DELETE FROM lab_login_web
        WHERE  ds_usuario_web = cd_pessoa_fisica_w
          AND  ie_tipo_login = 'P';

    END IF;

    IF (nr_crm_p IS NOT NULL AND nr_crm_p::text <> '')
    AND (uf_crm_p IS NOT NULL AND uf_crm_p::text <> '') THEN
        DELETE FROM lab_login_web
        WHERE  ds_usuario_web = nr_crm_uf_w
        AND    ie_tipo_login = 'CCRM';

    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
