SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_result_item_afterpost ( nr_seq_exame_p bigint, nr_sequencia_p bigint, nr_seq_resultado_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w	bigint;
nr_seq_metodo_w		bigint;
nr_seq_unid_med_w	bigint;
ds_unid_med_w		varchar(40);

BEGIN
if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_resultado_p IS NOT NULL AND nr_seq_resultado_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),null)
	into STRICT	nr_seq_material_w
	from (	SELECT	a.nr_sequencia
			from	material_exame_lab a,
				exame_lab_material b
			where	a.nr_sequencia = b.nr_seq_material
			and	b.nr_seq_exame = nr_seq_exame_p
			order by ie_prioridade) alias6 LIMIT 1;

	select	coalesce(max(nr_sequencia),null)
	into STRICT	nr_seq_metodo_w
	from (  	SELECT	a.nr_sequencia
			from	metodo_exame_lab a,
				exame_lab_metodo b
			where 	a.nr_sequencia = b.nr_seq_metodo
			and	b.nr_seq_exame = nr_seq_exame_p
			order by ie_prioridade) alias2 LIMIT 1;

	select	coalesce(max(a.nr_sequencia),null)
	into STRICT	nr_seq_unid_med_w
	from	lab_unidade_medida a,
		exame_laboratorio b
	where	a.nr_sequencia = b.nr_seq_unid_med
	and	b.nr_seq_exame = nr_seq_exame_p;

	if (nr_seq_unid_med_w IS NOT NULL AND nr_seq_unid_med_w::text <> '') then
		begin
		select	coalesce(max(a.ds_unidade_medida),null)
		into STRICT	ds_unid_med_w
		from	lab_unidade_medida a
		where	a.nr_sequencia = nr_seq_unid_med_w;
		end;
	end if;

	update	exame_lab_result_item
	set	nr_seq_material		= coalesce(nr_seq_material_w,nr_seq_material),
		nr_seq_metodo		= coalesce(nr_seq_metodo_w,nr_seq_metodo),
		nr_seq_unid_med		= coalesce(nr_seq_unid_med_w,nr_seq_unid_med),
		ds_unidade_medida	= coalesce(ds_unid_med_w,ds_unidade_medida),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_resultado	= nr_seq_resultado_p
	and	nr_sequencia		= nr_sequencia_p
	and	nr_seq_exame		= nr_seq_exame_p;

	CALL gerar_ref_result_lab_ext(nr_sequencia_p, nr_seq_resultado_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
