CREATE OR REPLACE PROCEDURE lab_result_item_beforepost ( nr_seq_exame_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_material_w	bigint;
ie_solicitado_w       varchar(1);


BEGIN
if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),null)
	into STRICT	nr_seq_material_w
	from (	SELECT	a.nr_sequencia
			from	material_exame_lab a,
				exame_lab_material b
			where	a.nr_sequencia = b.nr_seq_material
			and	b.nr_seq_exame = nr_seq_exame_p
			order by ie_prioridade) alias4 LIMIT 1;

    select	IE_SOLICITACAO
    into STRICT ie_solicitado_w
    from	exame_laboratorio
    where nr_seq_exame = nr_seq_exame_p;

    if (ie_solicitado_w = 'S' and coalesce(nr_seq_material_w::text, '') = '') then
    	CALL Wheb_mensagem_pck.exibir_mensagem_abort(374626);
    end if;
	end;
end if;
commit;
end;
