CREATE OR REPLACE PROCEDURE lab_update_dt_integracao ( nm_usuario_p text, ie_status_atend_p bigint, ie_status_pos_envio_p bigint, nr_prescricao_p bigint, cd_sigla_equip_p text, cd_setor_atendimento_p bigint, ie_status_envio_p bigint, ie_opcao_p bigint) AS $body$
BEGIN

if (ie_opcao_p = 1) then
	update	prescr_procedimento
	set	dt_integracao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ie_status_atend = ie_status_atend_p
	where	nr_prescricao = nr_prescricao_p
	and	(obter_Equipamento_Exame(nr_seq_exame,null,cd_sigla_equip_p) IS NOT NULL AND (obter_Equipamento_Exame(nr_seq_exame,null,cd_sigla_equip_p))::text <> '')
	and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and	cd_setor_atendimento = cd_setor_atendimento_p
	and	ie_status_atend = ie_status_envio_p
	and	coalesce(dt_integracao::text, '') = '';
elsif (ie_opcao_p = 2) then
	update	prescr_procedimento
	set	dt_integracao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ie_status_atend = ie_status_pos_envio_p
	where	nr_prescricao = nr_prescricao_p
	and	(obter_Equipamento_Exame(nr_seq_exame,null,cd_sigla_equip_p) IS NOT NULL AND (obter_Equipamento_Exame(nr_seq_exame,null,cd_sigla_equip_p))::text <> '')
	and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and	ie_status_atend = ie_status_envio_p
	and	coalesce(dt_integracao::text, '') = '';
end if;

commit;

end;
$body$
