SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lab_verifica_conta_definitiva (nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
qt_conta_prov_w		integer;
qt_conta_def_w		integer;
nr_atendimento_w	bigint;


BEGIN	 
			 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
			 
select	count(*) 
into STRICT	qt_conta_prov_w 
from	conta_paciente 
where	nr_atendimento		= nr_atendimento_w 
and	ie_status_acerto	= 1;
 
select	count(*) 
into STRICT	qt_conta_def_w 
from	conta_paciente 
where	nr_atendimento		= nr_atendimento_w 
and	ie_status_acerto	= 2;
 
if (qt_conta_prov_w = 0) and (qt_conta_def_w > 0) then 
	--(-20011, 'A conta do atendimento está com status definitivo. O procedimento não pode ser executado!'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263994);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
