CREATE OR REPLACE PROCEDURE latam_atualizar_valor_plano ( vl_hora_p bigint, nr_seq_gap_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
qt_programador_w		bigint;
qt_tempo_horas_w		bigint;
vl_custo_w				double precision;

c01 CURSOR FOR
SELECT	nr_sequencia,
		qt_programador,
		qt_tempo_horas
from	latam_requisito
where	(vl_hora IS NOT NULL AND vl_hora::text <> '')
and		nr_seq_gap	= nr_seq_gap_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	qt_programador_w,
	qt_tempo_horas_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	vl_custo_w	:= qt_programador_w * vl_hora_p * qt_tempo_horas_w;

	update	latam_requisito
	set		vl_custo		= vl_custo_w,
			VL_HORA			= vl_hora_p
	where	nr_sequencia	= nr_sequencia_w;

	commit;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
