CREATE OR REPLACE PROCEDURE latam_req_cancelar ( nr_sequencia_p LATAM_REQUISITO.NR_SEQUENCIA%TYPE, ds_justificativa_p LATAM_REQUISITO.DS_JUSTIFICATIVA%TYPE, nm_usuario_p LATAM_REQUISITO.NM_USUARIO%type, ie_origin_p text DEFAULT NULL, cd_justificativa_p LATAM_REQUISITO.CD_JUSTIFICATIVA%type default null) AS $body$
DECLARE

    dt_sysdate_s timestamp := clock_timestamp();
    ie_liberar_desenv_old_w LATAM_REQUISITO.IE_LIBERAR_DESENV%type;
    ie_tipo_hist_s          CONSTANT  LATAM_REQUISITO_HISTORICO.IE_TIPO_HIST%TYPE := '3';
    ie_situacao_ativo_s     CONSTANT  LATAM_REQUISITO_HISTORICO.IE_SITUACAO%TYPE := 'A';

BEGIN
IF ((nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') AND ((cd_justificativa_p IS NOT NULL AND cd_justificativa_p::text <> '') OR (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> ''))) THEN

  SELECT  IE_LIBERAR_DESENV
  INTO STRICT    ie_liberar_desenv_old_w
  FROM    LATAM_REQUISITO
  WHERE   NR_SEQUENCIA      = nr_sequencia_p;

  IF ie_origin_p = 'GAP' AND ie_liberar_desenv_old_w NOT IN ('A', 'B', 'D', 'G', 'F', 'K', 'N', 'P', 'S') THEN
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1186100, 'IE_LIBERAR_DESENV=' || obter_valor_dominio(8158,  ie_liberar_desenv_old_w));
  END IF;

  UPDATE  LATAM_REQUISITO
  SET     DT_ATUALIZACAO    = dt_sysdate_s,
          DT_INATIVACAO     = dt_sysdate_s,
          DS_JUSTIFICATIVA  = ds_justificativa_p,
          CD_JUSTIFICATIVA  = cd_justificativa_p,
          NM_USUARIO        = nm_usuario_p,
          IE_LIBERAR_DESENV = 'C'
  WHERE   NR_SEQUENCIA      = nr_sequencia_p;

  CALL generate_latam_log(nr_sequencia_p, ie_liberar_desenv_old_w, 1060959);

      INSERT INTO LATAM_REQUISITO_HISTORICO(
        NR_SEQUENCIA,
        DT_ATUALIZACAO,
        NM_USUARIO,
        DT_ATUALIZACAO_NREC,
        NM_USUARIO_NREC,
        IE_LIBERAR_DESENV,
        DT_LIBERACAO,
        DS_HISTORICO,
        NR_SEQ_REQUISITO,
        IE_SITUACAO,
        IE_TIPO_HIST)
        VALUES (
        nextval('latam_requisito_historico_seq'),
        dt_sysdate_s,
        nm_usuario_p,
        dt_sysdate_s,
        nm_usuario_p,
        ie_liberar_desenv_old_w,
        dt_sysdate_s,
        ds_justificativa_p,
        nr_sequencia_p,
        ie_situacao_ativo_s,
        ie_tipo_hist_s);

  COMMIT;
END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
