CREATE OR REPLACE PROCEDURE laudo_paciente_beforeedit ( nr_sequencia_p bigint, cd_setor_usuario_p bigint, ie_medico_p text, cd_setor_atendimento_p bigint, nm_usuario_p text, ie_permite_Alterar_Laudo_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

			 
alt_liberar_w		varchar(1) := 'N';
ie_setor_usuario_w		varchar(1) := 'N';
ie_medico_laudante_w	varchar(1) := 'N';
ie_permite_med_alterar_w	varchar(1) := 'N';
cd_estab_w		bigint;

BEGIN
 
alt_liberar_w := obter_param_usuario(28, 80, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, alt_liberar_w);
ie_permite_med_alterar_w := obter_param_usuario(28, 100, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_med_alterar_w);
ie_setor_usuario_w 	:= obter_se_setor_usuario(cd_setor_usuario_p,nm_usuario_p);
ie_medico_laudante_w	:= obter_se_medico_laudante(nr_sequencia_p,nm_usuario_p,'0');
 
if (cd_setor_usuario_p IS NOT NULL AND cd_setor_usuario_p::text <> '') and 
	((alt_liberar_w = 'S' AND cd_setor_usuario_p <> cd_setor_atendimento_p) or 
	(alt_liberar_w = 'T' AND  ie_setor_usuario_w = 'N')) then 
	begin 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(41999);
	end;
end if;
 
if (ie_permite_med_alterar_w = 'N') and (ie_medico_p = 'S') and (ie_medico_laudante_w = 'N') then 
	begin 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(42000);
	end;
end if;
 
select	max(cd_estabelecimento) 
into STRICT	cd_estab_w 
from	atendimento_paciente a, 
	laudo_paciente b 
where	a.nr_atendimento = b.nr_atendimento 
and	b.nr_sequencia   = nr_sequencia_p;
 
if (ie_permite_Alterar_Laudo_p = 'S') and (cd_estabelecimento_p <> cd_estab_w) then 
	begin 
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(118508);
	end;
end if;
 
end;
