SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ler_processo_adep ( nr_seq_processo_p bigint, ie_ler_p text, nm_usuario_p text) AS $body$
DECLARE

			 
dt_processo_w			timestamp;
dt_dispensacao_w		timestamp;
dt_retirada_w			timestamp;			
ie_status_processo_w	varchar(15);
ie_solucao_iniciada_w	varchar(5);
nr_etapa_sol_w			bigint;


BEGIN 
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (ie_ler_p IS NOT NULL AND ie_ler_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	 
	if (ie_ler_p = 'S') then 
 
		select	coalesce(max('S'), 'N') 
		into STRICT	ie_solucao_iniciada_w 
		from	prescr_mat_hor 
		where	(dt_inicio_horario IS NOT NULL AND dt_inicio_horario::text <> '') 
		and		ie_agrupador = 4 
		and		nr_seq_processo = nr_seq_processo_p;
 
		if (ie_solucao_iniciada_w = 'S') then 
			-- A solução já foi iniciada.		 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(206831);
		end if;
		 
		CALL atualiza_status_processo_adep(nr_seq_processo_p, null, 'A', 'C', clock_timestamp(), nm_usuario_p);
		 
	elsif (ie_ler_p = 'N') then 
	 
		CALL atualiza_status_processo_adep(nr_seq_processo_p, null, 'A', 'RC', clock_timestamp(), nm_usuario_p);
	 
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
