CREATE OR REPLACE PROCEDURE lfpd_gerar_reg_abertura ( nr_seq_controle_p bigint, nm_usuario_p text, cd_registro_p text, ie_movimento_p text, cd_municipio_p text, sg_estado_p text, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


/*
Se o identificador de movimento for igual a 'S', então o bloco possui movimento e deve ser informado zero.
Caso contrário, nõa possui movimento e deve ser informado um.
*/
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w	bigint := nr_sequencia_p;
sep_w 			varchar(1) := ds_separador_p;


BEGIN

if (ie_movimento_p = 'S') then

	if (coalesce(cd_municipio_p, 0) <> '0') then
		ds_linha_w := sep_w || cd_registro_p || sep_w || '0' || sep_w || cd_municipio_p || sep_w;
	elsif (coalesce(sg_estado_p, 0) <> '0') then
		ds_linha_w := sep_w || cd_registro_p || sep_w || '0' || sep_w || sg_estado_p || sep_w;
	else
		ds_linha_w := sep_w || cd_registro_p || sep_w || '0' || sep_w;
	end if;

else

	if (coalesce(cd_municipio_p, '0') <> '0') then
		ds_linha_w := sep_w || cd_registro_p || sep_w || '1' || sep_w || cd_municipio_p || sep_w;
	elsif (coalesce(sg_estado_p, '0') <> '0') then
		ds_linha_w := sep_w || cd_registro_p || sep_w || '1' || sep_w || sg_estado_p || sep_w;
	else
		ds_linha_w := sep_w || cd_registro_p || sep_w || '1' || sep_w;
	end if;

end if;

ds_arquivo_w		:= substr(ds_linha_w,1,4000);
ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
nr_seq_registro_w	:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;

insert into fis_lfpd_arquivo(	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_linha,
					ds_arquivo,
					ds_arquivo_compl,
					cd_registro,
					nr_seq_controle_lfpd)
			values (	nextval('fis_lfpd_arquivo_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_linha_w,
					ds_arquivo_w,
					ds_arquivo_compl_w,
					cd_registro_p,
					nr_seq_controle_p);
commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
