SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lfpd_registro_0100 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE

 
contador_w		bigint := 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w	bigint := nr_sequencia_p;
sep_w			varchar(1) := ds_separador_p;

cd_cgc_w		varchar(14);
cd_empresa_w		smallint;
cd_municipio_w		varchar(10);

c01 CURSOR FOR 
	SELECT	distinct 
		'0100' 										cd_registro, 
		CASE WHEN d.cd_cgc='' THEN p.nm_pessoa_fisica  ELSE d.ds_razao_social END  			nm_contabilista, 
		d.cd_cgc									cd_cgc, 
		CASE WHEN d.cd_cgc='' THEN p.nr_cpf  ELSE '' END 							nr_cpf, 
		e.nr_crc									nr_crc,	 
		b.sg_estado									sg_estado, 
		b.cd_cep									cd_cep, 
		b.ds_endereco									ds_endereco, 
		b.nr_endereco									nr_endereco, 
		b.ds_complemento								ds_complemento, 
		b.ds_bairro									ds_bairro, 
		substr(elimina_caracteres_telefone(b.nr_ddd_telefone || b.nr_telefone),1,10) 	nr_telefone, 
		substr(elimina_caracteres_telefone(b.nr_ddd_fax || b.nr_fax),1,10) 		nr_fax, 
		coalesce(c.ds_email, b.ds_email)							ds_email, 
		obter_dados_pf_pj(e.cd_contabilista, b.cd_cgc,'CDMDV') 			cd_localidade 
	FROM pessoa_fisica p, compl_pessoa_fisica c, estabelecimento a
LEFT OUTER JOIN pessoa_juridica b ON (a.cd_cgc = b.cd_cgc)
, empresa e
LEFT OUTER JOIN pessoa_juridica d ON (e.cd_cnpj_escritorio_ctb = d.cd_cgc)
WHERE e.cd_empresa	= a.cd_empresa  and e.cd_contabilista = p.cd_pessoa_fisica and p.cd_pessoa_fisica = c.cd_pessoa_fisica and b.ie_situacao	= 'A' and e.cd_empresa = cd_empresa_w and a.cd_estabelecimento = cd_estabelecimento_p and c.ie_tipo_complemento = 1;

vet01	c01%RowType;


BEGIN 
 
select	cd_empresa 
into STRICT	cd_empresa_w 
from	estabelecimento 
where	cd_estabelecimento = cd_estabelecimento_p;
 
open c01;
loop 
fetch c01 into	 
	vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	cd_municipio_w := vet01.cd_localidade;
	 
	if (vet01.sg_estado = 'DF') then 
		cd_municipio_w := '5300108';
	end if;
	 
	contador_w := contador_w + 1;
	 
	ds_linha_w	:= substr(	sep_w || vet01.cd_registro 		|| 
					sep_w || vet01.nm_contabilista 		|| 
					sep_w || vet01.cd_cgc 			|| 
					sep_w || vet01.nr_cpf 			|| 
					sep_w || vet01.nr_crc 			|| 
					sep_w || vet01.sg_estado 		|| 
					sep_w || vet01.cd_cep 			|| 
					sep_w || vet01.ds_endereco 		|| 
					sep_w || vet01.nr_endereco 		|| 
					sep_w || vet01.ds_complemento 		|| 
					sep_w || vet01.ds_bairro 		|| 
					sep_w || ''		 		|| 
					sep_w || ''		 		|| 
					sep_w || vet01.nr_telefone 		|| 
					sep_w || vet01.nr_fax			|| 
					sep_w || vet01.ds_email 		|| sep_w, 1, 8000);
	 
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;
	 
	insert into fis_lfpd_arquivo(	nr_sequencia, 
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						nr_linha, 
						ds_arquivo, 
						ds_arquivo_compl, 
						cd_registro, 
						nr_seq_controle_lfpd) 
				values (	nextval('fis_lfpd_arquivo_seq'), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						nr_linha_w, 
						ds_arquivo_w, 
						ds_arquivo_compl_w, 
						vet01.cd_registro, 
						nr_seq_controle_p);
 
	if (mod(contador_w,100) = 0) then 
		commit;
	end if;
	 
	end;
end loop;
close c01;
	 
commit;
 
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lfpd_registro_0100 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

