SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lfpd_registro_0125 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


contador_w		bigint := 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w	bigint := nr_sequencia_p;
sep_w			varchar(1) := ds_separador_p;

cd_cgc_w		varchar(14);
cd_empresa_w		smallint;

c01 CURSOR FOR
	SELECT	distinct
		'0125' 										cd_registro,
		p.nm_pessoa_fisica								nm_empresa_resp,
		''										cd_cgc_resp,
		p.nr_cpf									nr_cpf_resp,
		substr(elimina_caracteres_telefone(b.nr_ddd_telefone || b.nr_telefone),1,10) 	nr_telefone_resp,
		substr(elimina_caracteres_telefone(b.nr_ddd_fax || b.nr_fax),1,10) 		nr_fax_resp,
		c.ds_email									ds_email_resp
	from	pessoa_juridica b,
		pessoa_fisica p,
		compl_pessoa_fisica c,
		estabelecimento a,
		empresa e
	where	a.cd_cgc	= b.cd_cgc
	and	e.cd_empresa	= a.cd_empresa
	and	e.cd_contabilista = p.cd_pessoa_fisica
	and	p.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	e.cd_empresa = cd_empresa_w
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and	c.ie_tipo_complemento = 1;

vet01	c01%RowType;


BEGIN

select	cd_empresa
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_p;

open c01;
loop
fetch c01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	contador_w := contador_w + 1;

	ds_linha_w	:= substr(	sep_w || vet01.cd_registro 		||
					sep_w || vet01.nm_empresa_resp 		||
					sep_w || vet01.cd_cgc_resp		||
					sep_w || vet01.nr_cpf_resp		||
					sep_w || vet01.nr_telefone_resp		||
					sep_w || vet01.nr_fax_resp		||
					sep_w || vet01.ds_email_resp		|| sep_w, 1, 8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_lfpd_arquivo(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_linha,
						ds_arquivo,
						ds_arquivo_compl,
						cd_registro,
						nr_seq_controle_lfpd)
				values (	nextval('fis_lfpd_arquivo_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_linha_w,
						ds_arquivo_w,
						ds_arquivo_compl_w,
						vet01.cd_registro,
						nr_seq_controle_p);

	if (mod(contador_w,100) = 0) then
		commit;
	end if;

	end;
end loop;
close c01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
