CREATE OR REPLACE PROCEDURE lfpd_registro_9990 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


contador_w		bigint	:= 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w	bigint	:= nr_sequencia_p;
sep_w			varchar(1)	:= ds_separador_p;

cd_cgc_w		varchar(14);
qt_registros_w		bigint;


BEGIN

select	count(*) + 2
into STRICT	qt_registros_w
from	fis_lfpd_arquivo
where	nm_usuario = nm_usuario_p
and	substr(cd_registro,1,1) = '9';

ds_linha_w := 	substr(	sep_w	||	'9990'		|| sep_w ||
					qt_registros_w 	|| sep_w, 1, 8000);

ds_arquivo_w		:= substr(ds_linha_w,1,4000);
ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
nr_seq_registro_w	:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;

insert into fis_lfpd_arquivo(	nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_linha,
				ds_arquivo,
				ds_arquivo_compl,
				cd_registro,
				nr_seq_controle_lfpd)
		values (	nextval('fis_lfpd_arquivo_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_linha_w,
				ds_arquivo_w,
				ds_arquivo_compl_w,
				'9990',
				nr_seq_controle_p);

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
