CREATE OR REPLACE PROCEDURE lfpd_registro_a025 ( nr_seq_controle_p bigint, nr_seq_nota_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE

 
contador_w		bigint	:= 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w	bigint	:= nr_sequencia_p;
sep_w			varchar(1)	:= ds_separador_p;

c01 CURSOR FOR 
	SELECT	'A025' cd_registro, 
		0 vl_bc_irrf, 
		0 vl_aliq, 
		0 vl_retido_fonte, 
		replace(campo_mascara(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'X', 'PIS'),2),'.',',') aliq_pis, 
		replace(campo_mascara(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'PIS'),2),'.',',') vl_pis, 
		replace(campo_mascara(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'X', 'COFINS'),2),'.',',') aliq_cofins, 
		replace(campo_mascara(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'COFINS'),2),'.',',') vl_cofins, 
		replace(campo_mascara(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'X', 'INSS'),2),'.',',') vl_bc_prev, 
		replace(campo_mascara(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'V', 'INSS'),2),'.',',') vl_destacado_retido 
	from	nota_fiscal n 
	where	n.nr_sequencia = nr_seq_nota_p;

vet01	c01%RowType;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	contador_w := contador_w + 1;
 
	ds_linha_w := substr(	sep_w	||	vet01.cd_registro		|| sep_w || 
						vet01.vl_bc_irrf		|| sep_w || 
						vet01.vl_aliq			|| sep_w || 
						vet01.vl_retido_fonte		|| sep_w || 
						vet01.aliq_pis			|| sep_w || 
						vet01.vl_pis			|| sep_w || 
						vet01.aliq_cofins		|| sep_w || 
						vet01.vl_cofins			|| sep_w || 
						vet01.vl_bc_prev		|| sep_w || 
						vet01.vl_destacado_retido	|| sep_w,1, 8000);
 
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;
 
	insert into fis_lfpd_arquivo(	nr_sequencia, 
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						nr_linha, 
						ds_arquivo, 
						ds_arquivo_compl, 
						cd_registro, 
						nr_seq_controle_lfpd) 
				values (	nextval('fis_lfpd_arquivo_seq'), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						nr_linha_w, 
						ds_arquivo_w, 
						ds_arquivo_compl_w, 
						vet01.cd_registro, 
						nr_seq_controle_p);
 
	if (mod(contador_w,100) = 0) then 
		commit;
	end if;
 
	end;
end loop;
close C01;
 
commit;
 
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
