						ds_arquivo, 
						ds_arquivo_compl, 
						cd_registro, 
						nr_seq_controle_lfpd) 
				values (	nextval('fis_lfpd_arquivo_seq'), 
						clock_timestamp(), 
						nm_usuario_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						nr_linha_w, 
						ds_arquivo_w, 
						ds_arquivo_compl_w, 
						vet01.cd_registro, 
						nr_seq_controle_p);
 
	if (mod(contador_w,100) = 0) then 
		commit;
	end if;
 
	end;
end loop;
close C01;
 
commit;
 
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;
 
end;
