SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE lfpd_registro_b430 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


contador_w		bigint	:= 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w	bigint	:= nr_sequencia_p;
sep_w			varchar(1)	:= ds_separador_p;

c01 CURSOR FOR

SELECT 'B430' 													cd_registro,
		substr(ELIMINA_CARACTERE_ESPECIAL(cd_cfps), 1, 4) 						cd_cfps,
		replace(campo_mascara(sum(vl_contabil),2),'.',',') 						vl_contabil,
		replace(Campo_Mascara(sum(vl_base_calculo),2),'.',',') 						vl_base_calculo,
		'0,00' 												vl_materiais,
		'0,00' 												vl_subempreitada,
		replace(Campo_Mascara(sum(vl_nao_tributadas),2),'.',',') 					vl_nao_tributadas,
		replace(Campo_Mascara(sum(vl_base_calculo_deducao),2),'.',',')					vl_base_calculo_deducao,
		replace(Campo_Mascara(sum(vl_base_calculo_retencao),2),'.',',') 				vl_base_calculo_retencao,
		replace(Campo_Mascara(sum(vl_iss_retido),2),'.',',') 						vl_iss_retido,
		replace(Campo_Mascara(sum(vl_iss),2),'.',',') 							vl_iss
from (
SELECT	'B430' 													cd_registro,
		a.cd_cfps 											cd_cfps,
		n.vl_mercadoria 										vl_contabil,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS')					vl_base_calculo,
		'0,00' 												vl_materiais,
		'0,00' 												vl_subempreitada,
		n.vl_mercadoria - obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS')			vl_nao_tributadas,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'C', 'ISS')					vl_base_calculo_deducao,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISSST')					vl_base_calculo_retencao,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'ISSST')					vl_iss_retido,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'ISS') 					vl_iss
	FROM operacao_nota o, natureza_operacao a, nota_fiscal n
LEFT OUTER JOIN modelo_nota_fiscal m ON (n.nr_seq_modelo = m.nr_sequencia)
WHERE (m.cd_modelo_nf <> '3A' or coalesce(m.cd_modelo_nf::text, '') = '') and n.cd_operacao_nf = o.cd_operacao_nf and coalesce(Obter_se_nota_entrada_saida(n.nr_sequencia),'E') = 'S' and to_char(n.dt_emissao,'dd/MM/yy') between to_date(dt_inicio_p,'dd/MM/yy') and to_date(dt_fim_p,'dd/MM/yy') and n.cd_estabelecimento = cd_estabelecimento_p and (n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '') and o.ie_servico = 'S' and n.vl_total_nota > 0 and n.ie_situacao = 1 and exists (select 1
			from	nota_fiscal_item x
			where	x.nr_sequencia = n.nr_sequencia
			and		a.cd_natureza_operacao = x.cd_natureza_operacao
			and		(x.cd_procedimento IS NOT NULL AND x.cd_procedimento::text <> '')  LIMIT 1)

union all

select	'B430' 													cd_registro,
		a.cd_cfps 											cd_cfps,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS')	 				vl_contabil,
		0							 					vl_base_calculo,
		'0,00'												vl_materiais,
		'0,00' 												vl_subempreitada,
		n.vl_mercadoria - obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS') 			vl_nao_tributadas,
		obter_valor_tipo_tributo_nota(n.nr_sequencia, 'C', 'ISS')					vl_base_calculo_deducao,
		Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'B', 'ISS') 					vl_base_calculo_retencao,
		Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'V', 'ISS') 					vl_iss_retido,
		0							 					vl_iss
	FROM operacao_nota o, natureza_operacao a, nota_fiscal n
LEFT OUTER JOIN modelo_nota_fiscal m ON (n.nr_seq_modelo = m.nr_sequencia)
WHERE (m.cd_modelo_nf <> '3A' or coalesce(m.cd_modelo_nf::text, '') = '') and n.cd_operacao_nf = o.cd_operacao_nf and coalesce(Obter_se_nota_entrada_saida(n.nr_sequencia),'E') = 'E' and to_char(n.dt_emissao,'dd/MM/yy') between to_date(dt_inicio_p,'dd/MM/yy') and to_date(dt_fim_p,'dd/MM/yy') and n.cd_estabelecimento = cd_estabelecimento_p and (n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '') and o.ie_servico = 'S' and n.vl_total_nota > 0 and n.ie_situacao = 1 and exists (select 1
			from	nota_fiscal_item x
			where	x.nr_sequencia = n.nr_sequencia
			and		a.cd_natureza_operacao = x.cd_natureza_operacao
			and ((x.cd_material IS NOT NULL AND x.cd_material::text <> '') or (x.cd_procedimento IS NOT NULL AND x.cd_procedimento::text <> ''))  LIMIT 1)
	 ) alias56
	group by cd_cfps;

vet01	c01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	contador_w := contador_w + 1;

	ds_linha_w := substr(	sep_w	|| 	vet01.cd_registro		|| sep_w ||
						vet01.cd_cfps			|| sep_w ||
						vet01.vl_contabil		|| sep_w ||
						vet01.vl_base_calculo		|| sep_w ||
						vet01.vl_materiais		|| sep_w ||
						vet01.vl_subempreitada		|| sep_w ||
						vet01.vl_nao_tributadas		|| sep_w ||
						vet01.vl_base_calculo_deducao	|| sep_w ||
						vet01.vl_base_calculo_retencao	|| sep_w ||
						vet01.vl_iss_retido		|| sep_w ||
						vet01.vl_iss			|| sep_w, 1, 8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_lfpd_arquivo(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_linha,
						ds_arquivo,
						ds_arquivo_compl,
						cd_registro,
						nr_seq_controle_lfpd)
				values (	nextval('fis_lfpd_arquivo_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_linha_w,
						ds_arquivo_w,
						ds_arquivo_compl_w,
						vet01.cd_registro,
						nr_seq_controle_p);

	if (mod(contador_w,100) = 0) then
		commit;
	end if;

	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lfpd_registro_b430 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

