CREATE OR REPLACE PROCEDURE lfpd_registro_b440 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


contador_w		bigint	:= 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w		bigint	:= nr_sequencia_p;
sep_w			varchar(1)	:= ds_separador_p;

cd_cgc_w		varchar(14);
cd_tributo_iss_w		smallint;
nr_seq_regra_w		bigint;
nr_seq_regra_tributo_w	bigint;
pr_imposto_w		double precision;
vl_base_calculo_w	double precision;

c01 CURSOR FOR
	SELECT	'B440'													cd_registro,
		'0'									 				ie_operacao,
		coalesce(n.cd_pessoa_fisica, n.cd_cgc)									cd_participante,
		replace(Campo_Mascara(sum(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS')),2),'.',',')	vl_contabil_nota,
		(replace(Campo_Mascara(sum(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS')),2),'.',',')
		- replace(Campo_Mascara(sum(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'D', 'ISS')),2),'.',','))	vl_base_calculo,
		replace(Campo_Mascara(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'X', 'ISS'),2),'.',',')		pr_aliquota_iss,
		replace(Campo_Mascara(sum(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'V', 'ISS')),2),'.',',')	vl_retido
	from	nota_fiscal n,
		operacao_nota o
	where	n.cd_operacao_nf = o.cd_operacao_nf
	and (coalesce(Obter_se_nota_entrada_saida(n.nr_sequencia),'E') = 'E')
	and 	n.dt_emissao between dt_inicio_p and dt_fim_p
	and	n.cd_estabelecimento = cd_estabelecimento_p
	and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
	and 	o.ie_servico = 'S'
	and	n.vl_total_nota > 0
	and	n.ie_situacao = 1
	and	exists (SELECT	1
			from	nota_fiscal_item x
			where	x.nr_sequencia = n.nr_sequencia
			and 	(x.cd_material IS NOT NULL AND x.cd_material::text <> '')
			 LIMIT 1)
	group by 	coalesce(n.cd_pessoa_fisica, n.cd_cgc),
		replace(Campo_Mascara(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'X', 'ISS'),2),'.',',')
	
union

	select	'B440'													cd_registro,
		'1'									 				ie_operacao,
		coalesce(n.cd_pessoa_fisica, n.cd_cgc)									cd_participante,
		replace(Campo_Mascara(sum(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'B', 'ISS')),2),'.',',')		vl_contabil_nota,
		(replace(Campo_Mascara(sum(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'B', 'ISS')),2),'.',',')
		- replace(Campo_Mascara(sum(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'D', 'ISS')),2),'.',','))	vl_base_calculo,
		replace(Campo_Mascara(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'X', 'ISS'),2),'.',',')		pr_aliquota_iss,
		replace(Campo_Mascara(sum(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'V', 'ISS')),2),'.',',')		vl_retido
	from	nota_fiscal n,
		operacao_nota o
	where	n.cd_operacao_nf = o.cd_operacao_nf
	and (coalesce(Obter_se_nota_entrada_saida(n.nr_sequencia),'E') = 'S')
	and 	n.dt_emissao between dt_inicio_p and dt_fim_p
	and	n.cd_estabelecimento = cd_estabelecimento_p
	and	(n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '')
	and 	o.ie_servico = 'S'
	and	n.vl_total_nota > 0
	and	n.ie_situacao = 1
	and	exists (select	1
			from	nota_fiscal_item x
			where	x.nr_sequencia = n.nr_sequencia
			and 	(x.cd_procedimento IS NOT NULL AND x.cd_procedimento::text <> '') 
			 LIMIT 1)
	group by 	coalesce(n.cd_pessoa_fisica, n.cd_cgc),
		replace(Campo_Mascara(Obter_Valor_tipo_Trib_Retido(n.nr_sequencia, 'X', 'ISS'),2),'.',',');

vet01	c01%RowType;


BEGIN

select	nr_seq_regra_lfpd
into STRICT	nr_seq_regra_w
from	fis_lfpd_controle
where	nr_sequencia = nr_seq_controle_p;

select	cd_tributo_iss
into STRICT	cd_tributo_iss_w
from	fis_lfpd_regra
where	nr_sequencia = nr_seq_regra_w;

select	max(nr_sequencia)
into STRICT	nr_seq_regra_tributo_w
from	regra_calculo_imposto
where	cd_tributo = cd_tributo_iss_w;

select	pr_imposto
into STRICT	pr_imposto_w
from	regra_calculo_imposto
where	nr_sequencia = nr_seq_regra_tributo_w;

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	contador_w := contador_w + 1;
	if (vet01.vl_retido > 0 and vet01.pr_aliquota_iss > 0) then
		vl_base_calculo_w := (vet01.vl_base_calculo)::numeric;
	else
		vl_base_calculo_w := ('0,00')::numeric;
	end if;

	ds_linha_w := substr(	sep_w	||	vet01.cd_registro		|| sep_w ||
						vet01.ie_operacao		|| sep_w ||
						vet01.cd_participante	|| sep_w ||
						vet01.vl_contabil_nota	|| sep_w ||
						replace(Campo_Mascara(vl_base_calculo_w,2),'.',',')	|| sep_w ||
						vet01.pr_aliquota_iss	|| sep_w ||
						vet01.vl_retido		|| sep_w, 1, 8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_lfpd_arquivo(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_linha,
						ds_arquivo,
						ds_arquivo_compl,
						cd_registro,
						nr_seq_controle_lfpd)
				values (	nextval('fis_lfpd_arquivo_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_linha_w,
						ds_arquivo_w,
						ds_arquivo_compl_w,
						vet01.cd_registro,
						nr_seq_controle_p);

	if (mod(contador_w,100) = 0) then
		commit;
	end if;

	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE lfpd_registro_b440 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

