CREATE OR REPLACE PROCEDURE lfpd_registro_e360 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


contador_w		bigint	:= 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w	bigint	:= nr_sequencia_p;
sep_w			varchar(1)	:= ds_separador_p;

cd_cgc_w		varchar(14);

c01 CURSOR FOR
	SELECT	'E360'	cd_registro,
		'0,00'	vl_saidas_deb,
		'0,00'	vl_outros_deb,
		'0,00'	vl_estornos_cred,
		'0,00'	vl_debitos,
		'0,00'	vl_cred_entrada,
		'0,00'	vl_outros_cred,
		'0,00'	vl_estornos_deb,
		'0,00'	vl_subtotal_cred,
		'0,00'	vl_saldo_credor,
		'0,00'	vl_total_cred,
		'0,00'	vl_total_saldo_cred,
		'0,00'	vl_saldo_devedor,
		'0,00'	vl_deducoes,
		'0,00'	vl_icms_recolher,
		'0,00'	vl_icms_subs_entradas,
		'0,00'	vl_icms_subs_saidas,
		'0,00'	vl_aliq_icms,
		'0,00'	vl_icms_import,
		'0,00'	vl_outras_obrig_icms,
		'0,00'	vl_obrig_icms,
		'0,00'	vl_total_icms
	;

vet01	c01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	contador_w := contador_w + 1;

	ds_linha_w := substr(	sep_w ||	vet01.cd_registro		|| sep_w ||
						vet01.vl_saidas_deb		|| sep_w ||
						vet01.vl_outros_deb		|| sep_w ||
						vet01.vl_estornos_cred		|| sep_w ||
						vet01.vl_debitos		|| sep_w ||
						vet01.vl_cred_entrada		|| sep_w ||
						vet01.vl_outros_cred		|| sep_w ||
						vet01.vl_estornos_deb		|| sep_w ||
						vet01.vl_subtotal_cred		|| sep_w ||
						vet01.vl_saldo_credor		|| sep_w ||
						vet01.vl_total_cred		|| sep_w ||
						vet01.vl_total_saldo_cred	|| sep_w ||
						vet01.vl_saldo_devedor		|| sep_w ||
						vet01.vl_deducoes		|| sep_w ||
