CREATE OR REPLACE PROCEDURE lfpd_registro_h020 ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


contador_w		bigint	:= 0;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint	:= qt_linha_p;
nr_seq_registro_w	bigint	:= nr_sequencia_p;
sep_w			varchar(1)	:= ds_separador_p;

cd_cgc_w		varchar(14);

exi_ent			bigint := 0;

c01 CURSOR FOR
	SELECT	to_char(fim_mes(dt_mesano_referencia),'ddmmyyyy') dt_inventario,
		replace(Campo_Mascara_virgula(sum(vl_estoque)),'.','') vl_total_estoque
	from	saldo_estoque
	where	cd_estabelecimento = cd_estabelecimento_p
	and	trunc(dt_mesano_referencia) = trunc(dt_inicio_p)
	group by dt_mesano_referencia;

vet01	c01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;

if C01%NOTFOUND and exi_ent = 0 then
	vet01.dt_inventario	   := to_char(fim_mes(dt_inicio_p),'ddmmyyyy');
	vet01.vl_total_estoque := '0,00';

	ds_linha_w := substr(	sep_w	||	'H020'			|| sep_w ||
						vet01.dt_inventario	|| sep_w ||
						vet01.vl_total_estoque	|| sep_w, 1, 8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_lfpd_arquivo(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_linha,
						ds_arquivo,
						ds_arquivo_compl,
						cd_registro,
						nr_seq_controle_lfpd)
				values (	nextval('fis_lfpd_arquivo_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_linha_w,
						ds_arquivo_w,
						ds_arquivo_compl_w,
						'H020',
						nr_seq_controle_p);
end if;

EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	exi_ent := 1;
	contador_w := contador_w + 1;

	ds_linha_w := substr(	sep_w	||	'H020'			|| sep_w ||
						vet01.dt_inventario	|| sep_w ||
						vet01.vl_total_estoque	|| sep_w, 1, 8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_lfpd_arquivo(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_linha,
						ds_arquivo,
						ds_arquivo_compl,
						cd_registro,
						nr_seq_controle_lfpd)
				values (	nextval('fis_lfpd_arquivo_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_linha_w,
						ds_arquivo_w,
						ds_arquivo_compl_w,
						'H020',
						nr_seq_controle_p);

	if (mod(contador_w,100) = 0) then
		commit;
	end if;

	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
