CREATE OR REPLACE PROCEDURE liberar_acesso_dic_obj ( cd_funcao_p bigint, nm_usuario_acesso_p text, ie_lib_acesso_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lib_acesso_w	bigint;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nm_usuario_acesso_p IS NOT NULL AND nm_usuario_acesso_p::text <> '') and (ie_lib_acesso_p IS NOT NULL AND ie_lib_acesso_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_lib_acesso_w
	from	dic_obj_lib_acesso
	where	cd_funcao = cd_funcao_p
	and	nm_usuario_acesso = nm_usuario_acesso_p;

	if (nr_seq_lib_acesso_w = 0) then
		begin
		select	nextval('dic_obj_lib_acesso_seq')
		into STRICT	nr_seq_lib_acesso_w
		;

		insert into dic_obj_lib_acesso(
			nr_sequencia,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_atualizacao,
			nm_usuario,
			cd_funcao,
			nm_usuario_acesso,
			ie_lib_acesso)
		values (
			nr_seq_lib_acesso_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_funcao_p,
			nm_usuario_acesso_p,
			ie_lib_acesso_p);
		end;
	else
		begin
		update	dic_obj_lib_acesso
		set	ie_lib_acesso = ie_lib_acesso_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_lib_acesso_w;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
