SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_acesso_func_schematic (nr_seq_func_schematic_p bigint, nm_usuario_p text, nm_usuario_lib_p text, ie_acesso_p text) AS $body$
DECLARE


qt_w	bigint;


BEGIN

begin
select	1
into STRICT	qt_w
from	funcao_schematic_lib
where	nr_seq_funcao_schematic = nr_seq_func_schematic_p
and	nm_usuario_liberado = nm_usuario_lib_p  LIMIT 1;
exception
when others then
	qt_w := 0;
end;

if (qt_w > 0) then

	update	funcao_schematic_lib
	set	ie_acesso		= ie_acesso_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nm_usuario_liberado	= nm_usuario_lib_p
	and	nr_seq_funcao_schematic	= nr_seq_func_schematic_p;

else

	insert into funcao_schematic_lib(nr_sequencia,
		 dt_atualizacao,
		 nm_usuario,
		 dt_atualizacao_nrec,
		 nm_usuario_nrec,
		 nm_usuario_liberado,
		 nr_seq_funcao_schematic,
		 ie_acesso)
	values (nextval('funcao_schematic_lib_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_lib_p,
		nr_seq_func_schematic_p,
		ie_acesso_p);

end if;

commit;

end;
$body$
