CREATE OR REPLACE PROCEDURE liberar_adiantamento_pago ( nr_adiantamento_p adiantamento_pago.nr_adiantamento%type, nm_usuario_p usuario.nm_usuario%type, ie_opcao_p text default 'I') AS $body$
BEGIN

	if ((nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') and coalesce(ie_opcao_p,'I') = 'I') then

		update adiantamento_pago
		set dt_liberacao = clock_timestamp(),
		nm_usuario_lib = nm_usuario_p
		where nr_adiantamento = nr_adiantamento_p;

	elsif ((nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') and ie_opcao_p = 'E') then

		update adiantamento_pago
		set dt_liberacao  = NULL,
		nm_usuario_lib  = NULL
		where nr_adiantamento = nr_adiantamento_p;
		
	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
