SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_ajuste_estoque_consumo ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w			smallint;
dt_mesano_referencia_w			timestamp;
dt_mesano_vigente_w			timestamp;

BEGIN

select	cd_estabelecimento,
	dt_mesano_referencia
into STRICT	cd_estabelecimento_w,
	dt_mesano_referencia_w
from	sup_ajuste_estoque_consumo
where	nr_sequencia	= nr_sequencia_p;

select	dt_mesano_vigente
into STRICT	dt_mesano_vigente_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_w;

if (dt_mesano_referencia_w < dt_mesano_vigente_w) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266080,'DT_MESANO_REFERENCIA=' || dt_mesano_referencia_w);
	--'Não é permitido ajustar o custo médio num mês que já está fechado. ' ||
	--'Para efetuar esse ajuste, o mês vigente de  estoque deve ser ' || dt_mesano_referencia_w || '.');
end if;

update	sup_ajuste_estoque_consumo
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
