SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_alteracao_versao ( nr_sequencia_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
Opções

1 - Liberar - Desenvolvimento/Tecnologia
2 - Liberar cliente - Suporte
3 - Liberar suporte - Suporte
4 - Liberar projetos - Projetos

*/
BEGIN

if (ie_opcao_p = 1) then
	update	versao_alteracao
	set		dt_liberacao	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;

	CALL enviar_comunic_alt_versao(nr_sequencia_p, nm_usuario_p);
elsif (ie_opcao_p = 2) then
	update	versao_alteracao
	set		dt_lib_cliente	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p = 3) then
	update	versao_alteracao
	set		dt_liberacao_proj	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
elsif (ie_opcao_p = 4) then
	update	versao_alteracao
	set		dt_liberacao_sup	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
