CREATE OR REPLACE PROCEDURE liberar_anotacao ( nr_sequencia_p bigint, ie_tipo_evolucao_p text, nr_seq_atendimento_p bigint, nr_atendimento_p bigint, ds_evolucao_p text, ie_evolucao_clinica_p text, cd_profissional_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_msg_p INOUT text, nr_seq_evolucao_p INOUT bigint) AS $body$
DECLARE

 
ie_evolucao_quimioterapia_w	varchar(3);
cd_pessoa_fisica_w		varchar(10);
ds_param_out			bigint;


BEGIN 
 
update	paciente_atend_anot 
set	dt_liberacao	= clock_timestamp(), 
	nm_usuario_lib	= nm_usuario_p 
where	nr_sequencia	= nr_sequencia_p;
 
commit;
 
/* Quimioterapia - Parâmetro [102] - Tipo de evolução do paciente a ser gerada ao liberar anotação */
 
ie_evolucao_quimioterapia_w := obter_param_usuario(3130, 102, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_evolucao_quimioterapia_w);
 
if (ie_evolucao_quimioterapia_w <> 'N') then 
	begin 
	if (ie_tipo_evolucao_p IS NOT NULL AND ie_tipo_evolucao_p::text <> '') then 
		begin 
		select	max(coalesce(obter_dados_paciente_setor(b.nr_seq_paciente, 'C'),a.cd_pessoa_fisica)) 
		into STRICT	cd_pessoa_fisica_w 
		from	paciente_atend_anot a, 
			paciente_atendimento b 
		where	a.nr_seq_atendimento	= b.nr_seq_atendimento 
		and	a.nr_seq_atendimento	= nr_seq_atendimento_p;
 
		ds_param_out := gerar_evolucao_quimioterapia( 
			cd_pessoa_fisica_w, nr_atendimento_p, nm_usuario_p, ds_evolucao_p, ie_evolucao_clinica_p, ie_tipo_evolucao_p, cd_profissional_p, ds_param_out);
 
		nr_seq_evolucao_p := ds_param_out;
		end;
	else 
		ds_msg_p	:= substr(obter_texto_tasy(61606, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
