CREATE OR REPLACE PROCEDURE liberar_autor_carga_estoque ( nr_sequencia_p bigint, nm_usuario_p text, ds_base_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN

update	sup_autor_carga_estoque
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

CALL gerar_hist_autor_carga_estoque(
	nr_sequencia_p,
	'L',
	wheb_mensagem_pck.get_texto(690244),-- Liberação da autorização
	wheb_mensagem_pck.get_texto(690265),-- Foi liberada a autorização para carga de estoque.
	nm_usuario_p);

commit;

end;
