SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_avaliacao ( nr_seq_avaliacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_possui_aval_w 	varchar(2);
ie_possui_soap_w 	varchar(2) := 'N';
cd_pessoa_fisica_w MED_AVALIACAO_PACIENTE.CD_PESSOA_FISICA%TYPE;
nr_seq_avaliacao_w MED_AVALIACAO_PACIENTE.NR_SEQ_TIPO_AVALIACAO%TYPE;


BEGIN

select 	coalesce(max('S'),'N')
into STRICT 	ie_possui_aval_w
from 	med_avaliacao_result 
where 	nr_seq_avaliacao = nr_seq_avaliacao_p;

--if (ie_possui_aval_w = 'N') then

--	Wheb_mensagem_pck.exibir_mensagem_abort(881277); -- Conferir o preenchimento da avaliacao.

--end if;
update 	med_avaliacao_paciente
set 	dt_liberacao = clock_timestamp(),
	nm_usuario_lib = nm_usuario_p,
	nm_usuario = nm_usuario_p
where 	nr_sequencia = nr_seq_avaliacao_p;

commit;

Select 	coalesce(max('S'),'N')
into STRICT	ie_possui_soap_w
from	med_avaliacao_paciente
where	nr_Sequencia = nr_seq_avaliacao_p
and		(nr_Seq_soap IS NOT NULL AND nr_Seq_soap::text <> '');


if ( ie_possui_soap_w = 'S') then
	CALL Gerar_Evolucao_Avaliacao_soap(nr_seq_avaliacao_p,nm_usuario_p);
else
	CALL Gerar_Evolucao_Avaliacao_Pac(nr_seq_avaliacao_p,nm_usuario_p);
end if;

Gerar_Anamnese_Avaliacao_Pac(nr_seq_avaliacao_p,nm_usuario_p);

select coalesce(max(CD_PESSOA_FISICA), 0), coalesce(max(NR_SEQ_TIPO_AVALIACAO), 0)
into STRICT cd_pessoa_fisica_w, nr_seq_avaliacao_w
from MED_AVALIACAO_PACIENTE
where NR_SEQUENCIA = nr_seq_avaliacao_p;

CALL ATUALIZAR_EV_LINHA_CUIDADO('EV', 'MED_AVALIACAO_PACIENTE', nr_seq_avaliacao_p,
      'nr_seq_avaliacao_w='||nr_seq_avaliacao_w||';', cd_pessoa_fisica_w, nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
