SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_aval_hist_gv (nr_seq_hist_p bigint, nm_usuario_p text, ie_tipo_p text) AS $body$
BEGIN

if (nr_seq_hist_p IS NOT NULL AND nr_seq_hist_p::text <> '') then

	if (ie_tipo_p = 'L') then
		update	gestao_vaga_hist
		set	dt_liberacao 	     = clock_timestamp(),
			nm_usuario_liberacao = nm_usuario_p
		where	nr_sequencia	     = nr_seq_hist_p;
	end if;

	if (ie_tipo_p = 'D') then
		update	gestao_vaga_hist
		set	dt_liberacao 	      = NULL,
			nm_usuario_liberacao = ''
		where	nr_sequencia	     = nr_seq_hist_p;
	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
