CREATE OR REPLACE PROCEDURE liberar_aval_pre_anestesica ( nr_seq_avalicao_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_atendimento_w	bigint;


BEGIN 
 
if (nr_seq_avalicao_p > 0) then 
	update	aval_pre_anestesica 
	set	dt_liberacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_sequencia		= nr_seq_avalicao_p 
	and	coalesce(dt_liberacao::text, '') = '';
	commit;
	 
	select	coalesce(max(nr_atendimento),0) 
	into STRICT	nr_atendimento_w 
	from	aval_pre_anestesica 
	where	nr_sequencia = nr_seq_avalicao_p;
	 
	if (nr_atendimento_w > 0) then 
		CALL gerar_lancamento_automatico(nr_atendimento_w, null, 423, nm_usuario_p, null, nr_seq_avalicao_p, null, null, null, null);
	end if;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
