SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_aval_same ( nr_atendimento_p bigint, nm_usuario_p text, ie_opcao_p text, ds_erro_p INOUT text) AS $body$
BEGIN

If (ie_opcao_p = 'L') then
	update	avaliacao_paciente_cih
	set	dt_liberacao		= clock_timestamp(),
		nm_usuario_liberacao	= nm_usuario_p
	where	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_liberacao::text, '') = '';

	commit;
	ds_erro_p := exportar_aval_same(nr_atendimento_p, nm_usuario_p, ds_erro_p);
else
	update	avaliacao_paciente_cih
	set	dt_liberacao		 = NULL,
		nm_usuario_liberacao	 = NULL
	where	nr_atendimento = nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
