CREATE OR REPLACE PROCEDURE liberar_bc_atendimento ( nr_atendimento_p bigint, nm_usuario_p text, ie_opcao_p text default 'R') AS $body$
DECLARE



sql_errm_w				varchar(4000);	
nr_seq_procedimento_w	procedimento_pac_medico.nr_sequencia%type;
nr_seq_interno_w		diagnostico_doenca.nr_seq_interno%type;
										
C01 CURSOR FOR
	SELECT	nr_sequencia
	from	procedimento_pac_medico
	where	nr_atendimento = nr_atendimento_p
	and		nm_usuario = nm_usuario_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		coalesce(dt_inativacao::text, '') = '';
	

C02 CURSOR FOR
	SELECT	nr_seq_interno
	from	diagnostico_doenca
	where	nr_atendimento = nr_atendimento_p
	and		nm_usuario = nm_usuario_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		coalesce(dt_inativacao::text, '') = '';
	


BEGIN
begin

If ( ie_opcao_p = 'R' ) then

	open C01;
	loop
	fetch C01 into	
		nr_seq_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		CALL liberar_informacao('PROCEDIMENTO_PAC_MEDICO','NR_SEQUENCIA', nr_seq_procedimento_w,	nm_usuario_p );	
		
		end;
	end loop;
	close C01;
	
	open C02;
	loop
	fetch C02 into	
		nr_seq_interno_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		
		CALL liberar_informacao('DIAGNOSTICO_DOENCA','NR_SEQ_INTERNO', nr_seq_interno_w,	nm_usuario_p );	
		CALL gerar_log_diag_doenca(	nr_seq_interno_p => nr_seq_interno_w,	
								nm_usuario_p => nm_usuario_p,
								ie_acao_p => 'A');
		
		end;
	end loop;
	close C02;
	

end if;


exception
when others then
		sql_errm_w := substr(sqlerrm,1,4000);
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
