CREATE OR REPLACE PROCEDURE liberar_carta_autorizacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

				
dt_liberacao_w 	autorizacao_convenio_compl.dt_liberacao%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select 	dt_liberacao
	into STRICT	dt_liberacao_w
	from	autorizacao_convenio_compl
	where	nr_sequencia = nr_sequencia_p;
	
	if (coalesce(dt_liberacao_w::text, '') = '') then
		begin
		update	autorizacao_convenio_compl
		set	dt_liberacao = clock_timestamp(),
			nm_usuario_lib = nm_usuario_p
		where	nr_sequencia = nr_sequencia_p;
		commit;
		end;
	else
		begin
		update	autorizacao_convenio_compl
		set	dt_liberacao  = NULL,
			nm_usuario_lib  = NULL,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_sequencia_p;
		commit;
		end;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
