CREATE OR REPLACE PROCEDURE liberar_carta_correcao ( nr_seq_carta_p bigint, nm_usuario_p text, nr_sequencia_p bigint default null) AS $body$
BEGIN

if (coalesce(nr_sequencia_p::text, '') = '') then
	update	fis_carta_correcao
	set	dt_liberacao		= clock_timestamp(),
		nm_usuario_liberacao	= nm_usuario_p
	where	nr_sequencia		= nr_seq_carta_p;
else
	update	fis_carta_correcao
	set	dt_liberacao		= clock_timestamp(),
		nm_usuario_liberacao	= nm_usuario_p
	where	coalesce(dt_liberacao::text, '') = ''
	and	nr_seq_nota_fiscal		= nr_sequencia_p;
end if;

commit;

end;
$body$
