CREATE OR REPLACE PROCEDURE liberar_classificacao_sp (IE_OPCAO_P text, CD_VERSAO_P text, NR_SERVICE_PACK_P bigint, NR_PACOTE_P bigint, NR_OLD_BUILD_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


log_w varchar(4000);


BEGIN
    if (IE_OPCAO_P = 'L') then
        begin
            update man_service_pack_versao sp
            set sp.DT_LIBERACAO = clock_timestamp(),
            sp.nm_usuario = nm_usuario_p
            where sp.NR_SERVICE_PACK = NR_SERVICE_PACK_P
            and sp.NR_PACOTE = NR_PACOTE_P
            and sp.NR_OLD_BUILD = NR_OLD_BUILD_P;

            log_w := Expressao_Pck.Obter_Desc_Expressao(1044328);
        end;

         if (NR_SERVICE_PACK_P = 0) then
            update VERSION_MERGE vm
            set dt_version_release = clock_timestamp(),
                dt_atualizacao = clock_timestamp(),
                nm_usuario = nm_usuario_p
            where cd_versao = cd_versao_p;
        end if;
    else
        begin
            update man_service_pack_versao sp
            set sp.DT_LIBERACAO  = NULL,
    		    sp.nm_usuario = nm_usuario_p
            where sp.NR_SERVICE_PACK = NR_SERVICE_PACK_P
            and sp.NR_PACOTE = NR_PACOTE_P
            and sp.NR_OLD_BUILD = NR_OLD_BUILD_P;

            if (NR_SERVICE_PACK_P = 0) then
                update VERSION_MERGE vm
                set dt_version_release  = NULL,
                    dt_atualizacao = clock_timestamp(),
                    nm_usuario = nm_usuario_p
                where cd_versao = cd_versao_p;
            end if;

            log_w := Expressao_Pck.Obter_Desc_Expressao(1044330);
        end;
    end if;

    insert into log_classificacao_sp(
      nr_sequencia,
      nm_usuario,
      dt_atualizacao,
      cd_versao,
      nr_service_pack,
      ds_alteracao
    ) values (
      nextval('log_classificacao_sp_seq'),
      nm_usuario_p,
      clock_timestamp(),
      CD_VERSAO_P,
      nr_service_pack_p,
      log_w
    );

    commit;
END;
