SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE liberar_com_licenca ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_liberada_w	varchar(1) := 'N';


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_liberada_w
	from	com_licenca
	where	nr_sequencia = nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (ie_liberada_w = 'N') then
		begin
		update	com_licenca
		set	dt_liberacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
