CREATE OR REPLACE PROCEDURE liberar_consentimento (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;	
ie_gerar_evolucao_consent_w     varchar(1) := 'N';
cd_evolucao_w                   bigint;


BEGIN
ie_gerar_evolucao_consent_w := obter_param_usuario(281, 1631, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1), ie_gerar_evolucao_consent_w);

update	pep_pac_ci
set	dt_liberacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

begin
	select 	coalesce(max(nr_atendimento),0)
	into STRICT	nr_atendimento_w
	from 	pep_pac_ci
	where 	nr_sequencia = nr_sequencia_p;
exception
	when others then
	nr_atendimento_w:= 0;
end;

CALL gerar_lancamento_automatico(nr_atendimento_w, null, 551, nm_usuario_p, null,null,null,null,null,null);
if ( ie_gerar_evolucao_consent_w = 'S' ) THEN
    begin
        cd_evolucao_w := clinical_notes_pck.gerar_soap(nr_atendimento_w, nr_sequencia_p, 'CONSENT', null, 'P', 1, cd_evolucao_w);
         update pep_pac_ci
                set
                    cd_evolucao = cd_evolucao_w
                where
                    nr_sequencia = nr_sequencia_p;
    end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
